import {Component} from 'react';
import Swal from 'sweetalert2';
class SweetModal extends Component{

    htmlCode = "<>";
    componentDidMount(){
        setInterval(()=>{
            Swal.fire({
                icon:'question',
                title:'무엇을 찾을지 고민이신가요?',
                customClass: 'swal',
                html:
                    "이런 상품들을 관심 없으신가요?<br>" +
                    "<ul><li><a><a href='https://enterprise.kt.com/pd/P_PD_AI_RB_004.do'>AI서비스로봇</a></li></ul>",
                showConfirmButton:false,
                showCancelButton:true,
                background: '#fff',
                cancelButtonColor:'#4B088A',
                cancelButtonText:'닫기'
            });}
        ,2000);
    }

    render(){
        return(
            <></>
        )
    }
}

export default SweetModal;
