import {Component} from 'react';
import Swal from 'sweetalert2';
class SweetModal extends Component{
    componentDidMount(){
        setInterval(()=>{
            Swal.fire({
                icon:'question',
                title:'무엇을 찾을지 고민이신가요?',
                customClass: 'swal',
                body:"이런 상품들도 있어요",
                showConfirmButton:false,
                showCancelButton:true,
                background: '#fff',
                cancelButtonColor:'#4B088A',
                cancelButtonText:'닫기'
            });}
        ,2000);
    }

    render(){
        return(
            <></>
        )
    }
}

export default SweetModal;
