import {Modal} from 'reactstrap';

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      maxWidth:"250px",
      width: "100%",
      padding: "50px",
      fontSize: "20px"
    },  
  };

export const TimeoutWarningModal = ({isOpen, onRequestClose}) => {  
    const onCancel = () => {
      // Implement your logout functionality here
      window.location.href = "http://localhost:9000/"
    }
  
    return (
      <div> 
        <Modal
          isOpen={isOpen}
          style={customStyles}
        >
          <h2>Session Timeout</h2>
          <div>You're being timed out due to inactivity. Please choose to stay signed in or to logoff. Otherwise, you will be logged off automatically</div>
          <br/>
          <button onClick={onCancel}></button>
        </Modal>
      </div>  
    );
  }

export default TimeoutWarningModal;
