import { Component, useEffect } from "react";
import {Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';


useEffect(() => {
    const timer = setTimeout(() => console.log('Initial timeout!'), 1000);
  }, []);


class CustomModal extends Component {
    constructor(props){
        super(props)
        this.state = {modal:false};
    }

    handleOpen = () => {
        this.setState({modal:!this.state.modal})
    }

    render(){
        return(
            <>
                <Button color="warning" onClick={this.toggle}>Modal 버튼</Button>
                <Modal className="Modal" isOpen={this.state.modal} fade={true} toggle={this.toggle}>
                    <ModalHeader>
                        <p>저희는 이런 상품들도 있어요?</p>
                    </ModalHeader>
                    <ModalBody>
                        <ul>
                            <li>
                                <div className="m-keyword">
                                    오피스넷
                                </div>
                            </li>
                            <li>
                                <div className="m-keyword">
                                    비즈메카
                                </div>
                            </li>
                            <li>
                                <div className="m-keyword">
                                    랜선에듀
                                </div>
                            </li>
                            <li>
                                <div className="m-keyword">
                                    상권분석
                                </div>
                            </li>
                            <li>
                                <div className="m-keyword">
                                    코넷
                                </div>
                            </li>
                        </ul>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggle}>확인</Button>
                        <Button color="primary" onClick={this.toggle}>취소</Button>
                    </ModalFooter>
                </Modal>
            </>
        )
    }
}

export default CustomModal;
